(2529, 'tempera_settings', 'a:206:{s:12:"tempera_side";s:4:"3cSs";s:17:"tempera_sidewidth";s:3:"900";s:15:"tempera_sidebar";s:3:"250";s:14:"tempera_mobile";s:6:"Enable";s:15:"tempera_hheight";i:120;s:18:"tempera_siteheader";s:26:"Site Title and Description";s:18:"tempera_logoupload";s:0:"";s:23:"tempera_headermargintop";i:40;s:24:"tempera_headermarginleft";i:0;s:15:"tempera_favicon";s:0:"";s:25:"tempera_headerwidgetwidth";s:3:"33%";s:17:"tempera_frontpage";s:7:"Disable";s:18:"tempera_frontposts";s:6:"Enable";s:23:"tempera_frontpostscount";i:10;s:24:"tempera_frontpostsperrow";s:1:"2";s:21:"tempera_fpsliderwidth";i:1150;s:22:"tempera_fpsliderheight";i:400;s:20:"tempera_fpslideranim";s:6:"random";s:20:"tempera_fpslidertime";s:3:"750";s:21:"tempera_fpsliderpause";s:4:"5000";s:19:"tempera_fpslidernav";s:7:"Bullets";s:22:"tempera_fpsliderarrows";s:16:"Visible on Hover";s:27:"tempera_fpslider_bordersize";i:0;s:26:"tempera_fpslider_topmargin";i:0;s:17:"tempera_slideType";s:13:"Custom Slides";s:21:"tempera_slideSpecific";s:0:"";s:18:"tempera_slideCateg";s:0:"";s:19:"tempera_slideNumber";i:5;s:18:"tempera_sliderimg1";s:84:"http://orientreizen.eu.pn/wp-content/themes/tempera/images/slider/tempera-slide1.jpg";s:20:"tempera_slidertitle1";s:14:"Jump right in!";s:19:"tempera_slidertext1";s:280:"It''s like plunging into the calm ocean, the wind softly caressing your tanned body, the sun warming and igniting it. It''s like feeling the ocean cool and\r\n						embrace and contain you like a primordial womb. It''s also like waking up from this dream and getting back to your blog!";s:19:"tempera_sliderlink1";s:0:"";s:18:"tempera_sliderimg2";s:84:"http://orientreizen.eu.pn/wp-content/themes/tempera/images/slider/tempera-slide2.jpg";s:20:"tempera_slidertitle2";s:34:"Tempera brings the mountain to you";s:19:"tempera_slidertext2";s:203:"Remember that mountain you''ve always wanted to climb? Tempera will not only get it to come to you but it will also provide hiking equipment and if needed \